package types

import (
	"encoding/json"
	"testing"

	"github.com/candiddev/shared/go/assert"
)

type image struct {
	Image Image `json:"image"`
}

func TestImageUnmarshal(t *testing.T) {
	tests := map[string]struct {
		err   bool
		input string
		want  image
	}{
		"notanimage": {
			err:   true,
			input: `{"image":"this isn't an image"}`,
			want:  image{},
		},
		"null": {
			input: `{"image":null}`,
			want:  image{},
		},
		"image": {
			input: `{"image":"data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAABCAQAAAC1HAwCAAAAC0lEQVQYV2NgYAAAAAMAAWgmWQ0AAAAASUVORK5CYII="}`,
			want: image{
				Image: "data:image/jpeg;base64,",
			},
		},
	}

	for name, tc := range tests {
		t.Run(name, func(t *testing.T) {
			var c image

			assert.Equal(t, json.Unmarshal([]byte(tc.input), &c) != nil, tc.err)
			assert.Equal(t, c, tc.want)
		})
	}
}
